package com.avst.trm.v1.common.util.poiwork;

import com.avst.trm.v1.common.util.log.LogUtil;
import org.apache.commons.lang.StringUtils;
import org.apache.poi.poifs.filesystem.POIFSFileSystem;

import java.io.ByteArrayInputStream;
import java.io.FileOutputStream;
import java.io.InputStream;
import java.io.OutputStream;
import java.util.List;

/**
 * html标签转word
 */
public class HtmlToWord {

    /**
     *
     * @param wordpath 导出后word地址
     * @param htmlcontent 需要转word的html内容
     * @return
     */
    public static boolean HtmlToWord(String wordpath, String htmlcontent){
        if (StringUtils.isNotEmpty(wordpath)){
            htmlcontent = "<html><meta charset='UTF-8'><head></head><body>"+htmlcontent+"</body></html>";
            byte b[] = htmlcontent.getBytes();
            try {
                InputStream is = new ByteArrayInputStream(b);
                OutputStream os = new FileOutputStream(wordpath);
                POIFSFileSystem fs = new POIFSFileSystem();
                fs.createDocument(is, "WordDocument");
                fs.writeFilesystem(os);
                os.close();
                is.close();
                LogUtil.intoLog(4,WordToHtmlUtil.class,"HtmlToWord HTML转WORD成功"+wordpath);
                return  true;
            } catch (Exception e) {
                e.printStackTrace();
            }

        }
        LogUtil.intoLog(4,WordToHtmlUtil.class,"HtmlToWord HTML转WORD失败"+wordpath);
        return false;
    }

    public static void main(String[] args) {
        String content = "<div><p style='color: #999'>【赵六】 2019-11-02 13:31:12</p><span style='color: #fff; background: #0181cc;'>但是。</span></div>" +
                " <div><p style='color: #999'>2019-11-02 13:31:12 【赵六】 </p> <span  style='color: #fff; background: #ef8201;float:right'>八嘎。</span>  </div>";
        content = "<p style=\"text-align:center;\"><span style=\"font-family:&#39;方正小标宋简体&#39;;font-size:22.0pt;\">西</span><span style=\"font-family:&#39;方正小标宋简体&#39;;font-size:22.0pt;\"> 吉 县 人 民 法 院</span></p><p style=\"text-align:center;\"><span style=\"font-family:&#39;方正小标宋简体&#39;;font-size:24.0pt;\">法 庭 笔 录</span></p><p style=\"text-align:center;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">（简易程序）</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">时 &nbsp; &nbsp;间：</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\"> </span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">年</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\"> </span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">月</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\"> </span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">日</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\"> </span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">时</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\"> </span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">分至</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\"> </span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">时</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\"> </span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">分</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">地 &nbsp; &nbsp;点：西吉县人民法院兴隆法庭</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">案 &nbsp; &nbsp;号: 号</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">案 &nbsp; &nbsp;由: </span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">独任审判员：</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">书 &nbsp;记 &nbsp;员：</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">书 &nbsp;记 &nbsp;员:原告摆林强是否到庭?</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原告摆林强：到庭。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">书 记 员：被告马海平是否到庭？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被告马海平：到庭。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">书 &nbsp;记 &nbsp;员：大家安静，现在宣布法庭纪律(内容略)。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">书 &nbsp;记 &nbsp;员：全体起立，请审判员入庭。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">书 &nbsp;记 &nbsp;员：报告审判员，本案原告摆林强、被告马海平均已到庭。庭前准备工作已就绪，现在可以开庭。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审判员（以下简称审）：请坐下，（敲击法槌）现在开庭。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：首先核对当事人基本信息。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原告（以下简称&amp;ldquo;原&amp;rdquo;）:摆林强，男，1982年6月8日出生（公民身份号码642223198206083036），回族，宁夏西吉县人，小学文化，农民，住所地西吉县兴隆镇王河村五组。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被告（以下简称&amp;ldquo;被&amp;rdquo;）:马海平，男，1983年4月13日出生（公民身份号码642223198304133033），回族，宁夏西吉县人，初中文化，农民，住所地西吉县兴隆镇单南村五组。</span></p><p style=\"text-indent:31.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：原、被告有无委托诉讼代理人？</span></p><p style=\"text-indent:31.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：我没有委托诉讼代理人。</span></p><p style=\"text-indent:31.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：我没有委托诉讼代理人。</span></p><p style=\"text-indent:32.0pt;text-align:left;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">经核对</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">，</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">各方当事人均符合法律规定</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">，</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">可以参加本案诉讼活动</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">。</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">西吉县</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">人民法院兴隆法庭依照</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">《</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">中华人民共和国民事诉讼法</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">》</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">第三十九条第二款、第一百三十四条规定</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">，</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">今天依法适用简易程序</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">，</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">公开开庭审理</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">（</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">2019</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">）宁0422</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">民</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">初</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">3463</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">号原告摆林强与被告马海平健康权纠纷一案</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">。</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">本案由审判员苏文兵独任审理</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">，</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">由书记员</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">李旭伟</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">担任法庭记录</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">。</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原告摆林强、被告马海平到庭参加诉讼，原、被告对审判员和书记员名单是否听清楚了？</span></p><p style=\"text-indent:32.0pt;text-align:left;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：听清楚了。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：听清楚了。</span></p><p style=\"text-indent:32.0pt;text-align:left;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：根据《中华人民共和国民事诉讼法》有关规定，当事人享有广泛的诉讼权利，现就主要权利告知如下，希望当事人正确行使（内容略&amp;hellip;）。对于以上宣布的权利，原、被告是否听清楚了？</span></p><p style=\"text-indent:32.0pt;text-align:left;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：听清楚了。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：听清楚了。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：原、被告是否申请审判员和书记员回避？</span></p><p style=\"text-indent:32.0pt;text-align:left;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：不申请回避。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：不申请回避。</span></p><p style=\"text-indent:32.0pt;text-align:left;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：现在宣布当事人在诉讼中必须履行的义务（内容略&amp;hellip;）。对于以上宣读诉讼义务，原、被告是否听清楚了？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：听清楚了。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：听清楚了。</span></p><p style=\"text-indent:32.0pt;text-align:left;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：庭审中，原告未经法庭许可中途退庭的，按撤诉处理，被告未经法庭许可中途退庭的，按缺席审判；被告提起反诉的，原告未经法庭许可中途退庭的，按缺席审判。以上法律规定，原、被告是否听清楚了？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：听清楚了。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：听清楚了。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：原、被告是否申请证人出庭作证？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原: 不申请。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：不申请。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：下面进行法庭调查，首先由原告向法庭陈述你的诉讼请求、事实和理由，可以宣读民事诉状。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：诉讼请求：1.依法判令被告马海平赔偿原告医疗费、误工费、护理费、伙食补助费、交通费等共计7101.26元；2.本案诉讼费由被告承担。（事实和理由详见民事诉状）。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：原告，除诉状外，你的诉讼请求有无变更、增加的，事实和理由方面有无补充？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：没有了。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：下面由被告针对原告的诉讼请求、事实和理由进行答辩？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：我没有动手打原告，原告看的耳部、脑部、颈部、不是我打的，我只是把原告推搡了一下，原告住院的费用我不承担。 </span></p><p class=\"X1 X12\" style=\"text-indent:32.0pt;text-align:left;\"><span class=\"X1 X12\" style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：现在进行法庭举证、质证。根据《中华人民共和国民事诉讼法》第六十四条第一款之规定，当事人对自己提出的主张，有责任提供证据。举证时要将证据分类并说明证据来源和想要证明的问题，经过</span><span class=\"X1 X12\" style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">刚才原告的陈述、被告答辩，本庭认为原、被告举证时应当围绕：&amp;ldquo;1.被告是否对原告实施了侵权行为？2.原告主张的各项损失是否合理？是否有法律依据？3.原告在本案中是否有过错？4.原、被告在本案中的责任如何划分？&amp;rdquo;为焦点进行举证、质证，下面首先由原告向法庭举证，被告进行质证。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：证据一，西吉县人民医院住院病历一份，欲证明原告因本案在西吉县人民医院住院治疗7天的事实。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：被告对该证据有无异议？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：有异议，这个病历我不承认，我没有打原告脑部、耳部，耳聋与我无关。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：原告继续举证。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：证据二，医疗费票据七张，欲证明原告因本案住院治疗花去医疗费4447.46元、复印费24元的事实。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：被告对该证据有无异议？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：无异议。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：原告继续举证。</span>&nbsp;&nbsp;&nbsp;&nbsp;</p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：我的证据出示完毕。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：下面由被告出示证据，原告进行质证。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：我没有证据向法庭出示。 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：下面出示由法庭依职权调取的证据。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：证据一，受案登记表一份（宣读经过略），证实了案发时间、地点、事情经过、报案人等情况。 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：原、被告对该证据听清楚了吗？有无异议？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：听清楚了，无异议。</span></p><p style=\"text-align:left;text-indent:31.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：听清楚了，无异议。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：证据二，原告摆林强的陈述一份（宣读经过略），证实了案发时间、地点、人物、起因、经过、结果等事实。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：原、被告对该证据听清楚了吗？有无异议？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：听清楚了，无异议。</span></p><p style=\"text-align:left;text-indent:31.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：听清楚了，有异议，我没有直接推搡原告，原告当</span></p><p><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">时说让我嘴不要犟了，我就把原告推搡了一下。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：证据三，被告马海平的陈述一份（宣读经过略），证实了案发时间、地点、人物、起因、经过、结果等事实。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：原、被告对该证据听清楚了吗？有无异议？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：听清楚了，有异议，我没有说被告嘴不要犟了，我刚说有啥事协商解决。</span></p><p style=\"text-align:left;text-indent:31.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：听清楚了，有异议，我没有说从原告脖子推搡，我</span></p><p><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">只是从左肩膀推了一下。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：证据四，证人李文俊的陈述一份（宣读经过略），证实了案发时间、地点、人物、起因、经过、结果等事实。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：原、被告对该证据听清楚了吗？有无异议？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：听清楚了，无异议。</span></p><p style=\"text-align:left;text-indent:31.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：听清楚了，有异议，我没有从脖子搡，我是从左肩</span></p><p><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">膀搡了一下。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：证据五，行政处罚决定书及代收罚款专用票据各一份（宣读经过略），证实了被告马海平因本案被西吉县公安局处以行政罚款三百元的事实。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：原、被告对该证据听清楚了吗？有无异议？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：听清楚了，无异议。</span></p><p style=\"text-align:left;text-indent:31.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：听清楚了，无异议。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：法庭调取的证据出示完毕。</span></p><p style=\"text-indent:32.0pt;text-align:left;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">：</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">根据原告的诉讼请求、被告答辩以及证据交换情况</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">，</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">本庭归纳本案庭审争议焦点如下</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">：&amp;ldquo;</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">1.被告是否对原告实施了侵权行为？2.原告主张的各项损失具体为多少？是否合理？3.原告在本案中是否有过错？4.原被告在本案中的责任如何划分？&amp;rdquo;</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原、被告对本庭归纳的争议焦点是否有异议？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：无异议。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：无异议。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">：</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">下面围绕本案争议焦点涉及的事实问题展开调查</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">，你们要如实回答。</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原、被告</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">听清楚了吗？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：听清楚了。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：听清楚了。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审:原告，你在住院期间，被告给你有无垫付过医疗费？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：没有支付过医疗费。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：被告，原告刚才说的是否属实？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：属实。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：原告，你主张的误工费、护理费为2018年赔偿标准，应按2019年标准计算，你是什么意见？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：就按我主张的2018年标准计算。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：原告，被告打到你什么部位了？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：当时被告和李四八在争吵，我过去说你们不要吵了，有啥事协商解决，被告就说你皮不要犟了，就从我脖子推了一把。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：原告，你这次住院主要治疗什么疾病？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：脑部、耳部、和颈部。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：原告，被告打你耳部了吗？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：没有。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：原告，被告打你脑部了吗？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：没有。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：原告，被告把你是怎么推到的？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：被告把我推到在地，使我后脑部着地。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：法庭询问完毕。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：原、被告有无相互发问的。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：我没有发问的。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：有。</span></p><p style=\"text-indent:31.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：原告，你把你推到在地，地上有什么？</span></p><p style=\"text-indent:31.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：路上是挖的砂石和渣土。</span></p><p style=\"text-indent:31.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：被告继续发问。</span></p><p style=\"text-indent:31.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：我的发问完毕。</span></p><p style=\"text-indent:31.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：对原告出示及本院依职权调取并出示的证据经双方相互质证后无异议的证据，本庭予以认定，对双方质证后有异议的证据，经本院审查，上述证据与其他证据形成证据链条，本庭予以认定。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：法庭调查结束，</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">现在进行法庭辩论</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">。</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">法庭辩论阶段需要当事人发表法律意见的问题是</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">：</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">1.被告是否对原告实施了侵权行为？2.原告主张的各项损失具体为多少？是否合理？3.原告在本案中是否有过错？4.原被告在本案中的责任如何划分？</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">首先</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">由原告发表辩论意见？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：我没有辩论意见。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：下面由被告发表辩论意见？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：我没有打原告，原告看的病不是我打的，我错就错在搡原告了，原告主张的医疗费我承担不起。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：法庭辩论结束，现在进行法庭最后陈述，首先由原告进行最后陈述。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：没有。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：下面由被告发表最后陈述意见。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：没有。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：</span><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;color:#000000;\">法庭陈述结束。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：本案经原告陈述、被告答辩，原告举证、被告质证，法庭询问，法庭辩论和原、被告最后陈述，本案事实清楚，证据充分，依照《中华人民共和国民事诉讼法》第九条、第九十三条、第九十四条、第九十六条的规定，本案由法庭主持进行调解。原、被告是否同意调解？</span></p><p class=\"X1 X11\" style=\"text-indent:32.0pt;\"><span class=\"X1 X11\" style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原: 我同意调解。</span></p><p class=\"X1 X11\" style=\"text-indent:32.0pt;\"><span class=\"X1 X11\" style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：我同意调解。</span></p><p class=\"X1 X11\" style=\"text-indent:32.0pt;\"><span class=\"X1 X11\" style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：既然原、被告都同意调解，那么首先由原告提一个调解意见。</span></p><p class=\"X1 X11\" style=\"text-indent:32.0pt;\"><span class=\"X1 X11\" style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：我的意见是被告赔偿我各项损失共计6000元；本案诉讼费由被告承担。</span></p><p class=\"X1 X11\" style=\"text-indent:32.0pt;\"><span class=\"X1 X11\" style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：被告，你同意原告的调解意见吗？</span></p><p class=\"X1 X11\" style=\"text-indent:32.0pt;\"><span class=\"X1 X11\" style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：我不同意，我最后赔偿原告3000元；本案诉讼费由我承担。</span></p><p class=\"X1 X11\" style=\"text-indent:32.0pt;\"><span class=\"X1 X11\" style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：原告，你是否同意被告赔偿你3000元？</span></p><p class=\"X1 X11\" style=\"text-indent:32.0pt;\"><span class=\"X1 X11\" style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：我不同意。</span></p><p class=\"X1 X11\" style=\"text-indent:32.0pt;\"><span class=\"X1 X11\" style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：下面由法庭提一个调解意见，供你们双方参考：由被告赔偿原告各项损失共计4000元，原、被告是否同意？</span></p><p class=\"X1 X11\" style=\"text-indent:32.0pt;\"><span class=\"X1 X11\" style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原：我不同意。</span></p><p class=\"X1 X11\" style=\"text-indent:32.0pt;\"><span class=\"X1 X11\" style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">被：我不同意。</span></p><p class=\"X1 X11\" style=\"text-indent:32.0pt;\"><span class=\"X1 X11\" style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审：因原、被告分歧意见较大不能达成调解意见，故法庭不再主持调解，</span><span class=\"X1 X11\" style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">本案择期另行宣判，</span><span class=\"X1 X11\" style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">庭审笔录当庭核对，原、被告如认为对自己的陈述记录有遗漏或者差错的，有权申请补正。确认无误后，应在笔录上签名并按印。现在闭庭（敲击法槌）。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">书记员：全体起立，请审判员退庭（审判员退庭）。</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">书记员：以上笔录是否正确，如无误请签名、按印？</span></p><p style=\"text-indent:32.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">原： &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;被：</span></p><p style=\"text-indent:336.0pt;\"><br/></p><p style=\"text-indent:336.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">审判员：</span></p><p style=\"text-indent:336.0pt;\"><span style=\"font-family:&#39;仿宋&#39;;font-size:16.0pt;\">书记员：</span></p><p><br/></p>";
        byte b[] = content.getBytes();
        try {
            InputStream is = new ByteArrayInputStream(b);
            OutputStream os = new FileOutputStream("f:\\1.docx");
            POIFSFileSystem fs = new POIFSFileSystem();
            fs.createDocument(is, "WordDocument");
            fs.writeFilesystem(os);
            os.close();
            is.close();
        } catch (Exception e) {
            e.printStackTrace();
        }
    }


}
